mapper.select:
select hotplace_no, user_id, hotplace_title, hotplace_content, hotplace_like, hotplace_date, hotplace_image
from hotplace
where hotplace_no = #{hotplaceNo}

select hotplace_no, user_id, hotplace_title, hotplace_content, hotplace_like, hotplace_date, hotplace_image
from hotplace
where user_id = #{userId} and hotplace_title = #{title} and hotplace_content = #{content}

mapper.update:
update hotplace
set hotplace_like = hotplace_like + 1
where hotplace_no = #{hotplaceNo}

update hotplace
set hotplace_title = #{title}, hotplace_content = #{content}, hotplace_like = #{like}, hotplace_date = #{date}, hotplace_image =#{img} 
where hotplace_no = #{hotplaceNo}

mapper.select:
select save_folder, original_file, save_file
from file_info
where hotplace_no = #{hotplaceNo}
